    <main>
        <div class="flex-wrapper">



            <div class="container">
                <h1>Meter to Millimeters</h1>

                <div class="form-wrapper">
                    <input type="number" id="input" placeholder="Enter meters">
                    <button onclick="convertMeterToMillimeters()">Convert</button>
                </div>
                <p class="output">
                    In Millimeters : <span id="result"></span>
                </p>

                <canvas id="conversionChart"></canvas>
            </div>

            <script>
                function convertMeterToMillimeters() {
                    const input = document.getElementById("input").value;
                    const result = input * 1000;
                    document.getElementById("result").innerText = result.toFixed(4) + " mm";

                    const ctx = document.getElementById('conversionChart').getContext('2d');
                    const data = {
                        labels: ['Meters', 'Millimeters'],
                        datasets: [{
                            label: 'Conversion',
                            data: [input, result],
                            backgroundColor: [
                                'rgba(0, 91, 82, 0.2)',
                                'rgba(255, 99, 132, 0.2)',
                            ],
                            borderColor: [
                                'rgba(0, 91, 82, 1)',
                                'rgba(255, 99, 132, 1)', // Red
                            ],
                            borderWidth: 1.5
                        }]
                    };

                    const config = {
                        type: 'bar',
                        data: data,
                        options: {
                            responsive: true,
                            animation: {
                                duration: 1000,
                                easing: 'easeOutBounce'
                            },
                            scales: {
                                y: {
                                    beginAtZero: true
                                }
                            }
                        }
                    };

                    if (window.conversionChart instanceof Chart) {
                        window.conversionChart.destroy();
                    }

                    window.conversionChart = new Chart(ctx, config);
                }
                window.onload = function() {
                    convertMeterToMillimeters();
                };
                document.getElementById("input").addEventListener("keypress", function(event) {
                    if (event.key === "Enter") {
                        convertMeterToMillimeters();
                    }
                });
            </script>

