    <main>
        <div class="flex-wrapper">



            <div class="container">
                <h1>Square Meter to Square Yard</h1>

                <div class="form-wrapper">
                    <input type="number" id="input" placeholder="Enter square meters">
                    <button onclick="convertSquareMeterToSquareYard()">Convert</button>
                </div>
                <p class="output">
                    In Square Yards : <span id="result"></span>
                </p>

                <canvas id="conversionChart"></canvas>
            </div>
            
            <script>
                function convertSquareMeterToSquareYard() {
                    const input = document.getElementById("input").value;
                    const result = input * 1.19599;

                    document.getElementById("result").innerText = result.toFixed(4) + " sq. yards";

                    const ctx = document.getElementById('conversionChart').getContext('2d');
                    const data = {
                        labels: ['Square Meters', 'Square Yards'],

                        datasets: [{
                            label: 'Conversion',
                            data: [input, result],
                            backgroundColor: [
                                'rgba(0, 91, 82, 0.2)',
                                'rgba(255, 99, 132, 0.2)',
                            ],
                            borderColor: [
                                'rgba(0, 91, 82, 1)',
                                'rgba(255, 99, 132, 1)', // Red
                            ],
                            borderWidth: 1.5
                        }]
                    };

                    const config = {
                        type: 'bar',
                        data: data,
                        options: {
                            responsive: true,
                            animation: {
                                duration: 1000,
                                easing: 'easeOutBounce'
                            },
                            scales: {
                                y: {
                                    beginAtZero: true
                                }
                            }
                        }
                    };

                    if (window.conversionChart instanceof Chart) {
                        window.conversionChart.destroy();
                    }

                    window.conversionChart = new Chart(ctx, config);
                }
                window.onload = function() {
                    convertSquareMeterToSquareYard();
                };
                document.getElementById("input").addEventListener("keypress", function(event) {
                    if (event.key === "Enter") {
                        convertSquareMeterToSquareYard();
                    }
                });
            </script>

