                        7px 7px 20px 0px #0003,
                        4px 4px 5px 0px #0002;
                    transition: 400ms ease all;
                }

                .btn-5:after {
                    right: inherit;
                    top: inherit;
                    left: 0;
                    bottom: 0;
                }

                .btn-5:hover:before,
                .btn-5:hover:after {
                    width: 100%;
                    transition: 800ms ease all;
                }

